Classify the following data: 
Here is one by Andrew "Graphics Gems" Glassner that I got from a
collegue of mine.  I think I fiddled with it a little bit to make it
deal with whatever bizarre problem I was working on at the time but it
is known to work.

							spl
			       - - - -
/* spheres
    ASG     9 Feb 85
    spl Thu Mar  8 17:17:40 EST 1990
*/
#include <stdio.h>
#include <math.h>

#define PI 3.141592654

struct Point_struct {
        double x, y, z;
};

static double radius;
static double xorg;
static double yorg;
static double zorg;

do_sphere( r, freq, x, y, z )

  double r;
  int freq;
  double x;
  double y;
  double z;

  {

    int pole;
    double northy, southy, poley;
    double rtheta, rtheta2, ntheta, ntheta2, magicangle;
    double theta, thetastart, thisy, den, t;
    struct Point_node *pnp;
    struct Point_struct p1, p2, p3, p4, n1, n2, n3, n4, pt;

    radius = r;
    xorg = x;
    yorg = y;
    zorg = z;

/* north pole */

    magicangle = 30.0*PI/180.0;
    northy = radius*sin(magicangle);
    southy = -radius*sin(magicangle);
    for (pole=0; pole<2; pole++) {

      if (pole==0) {

        poley=radius;   
        thisy=northy; 
        thetastart=0.0; 

      }
      else { 

        poley= -radius; 
        thisy=southy; 
        thetastart=36.0; 

      }
      for ( theta = thetastart; theta < 360.0; theta += 60.0 ) {

        rtheta = theta*PI/180.0;
        rtheta2 = (theta+60.0)*PI/180.0;
        p1.x = 0.0;  
        p1.y = poley;  
        p1.z = 0.0;  
        p2.x = radius*cos(rtheta);
        p2.y = thisy;
        p2.z = radius*sin(rtheta);
        p3.x = radius*cos(rtheta2);
        p3.y = thisy;
        p3.z = radius*sin(rtheta2);

        if (pole==0) {

/* make ring go the other way so normals are right */

          pt.x = p3.x;  
          pt.y = p3.y;  
          pt.z = p3.z;  
          p3.x = p2.x;  
          p3.y = p2.y;  
          p3.z = p2.z;  
          p2.x = pt.x;  
          p2.y = pt.y;  
          p2.z = pt.z;  

        }

        den = (p1.x*p1.x)+(p1.y*p1.y)+(p1.z*p1.z);  
        den = sqrt(den);

        if (den != 0.0) {

          t = radius / den; 
          p1.x *= t;  
          p1.y *= t;  
          p1.z *= t;

        }

        den = (p2.x*p2.x)+(p2.y*p2.y)+(p2.z*p2.z);  
        den = sqrt(den);

        if (den != 0.0) {

          t = radius / den; 
          p2.x *= t;  
          p2.y *= t;  
          p2.z *= t;

        }

        den = (p3.x*p3.x)+(p3.y*p3.y)+(p3.z*p3.z);  
        den = sqrt(den);

        if (den != 0.0) {

          t = radius / den; 
          p3.x *= t;  
          p3.y *= t;  
          p3.z *= t;

        }

        subdivide_tri(&p1,&p2,&p3,freq);

      }

    }

/* now the body */

    for (theta=0.0; theta<360.0; theta += 60.0) {

      rtheta = theta*PI/180.0;  
      rtheta2 = (theta+60.0)*PI/180.0;
      ntheta = (theta+36.0)*PI/180.0; 
      ntheta2 = (theta+96.0)*PI/180.0;
      p1.x = radius*cos(rtheta);   
      p1.y = northy;  
      p1.z = radius*sin(rtheta);
      p2.x = radius*cos(rtheta2);  
      p2.y = northy;  
      p2.z = radius*sin(rtheta2);
      p3.x = radius*cos(ntheta);   
      p3.y = southy;  
      p3.z = radius*sin(ntheta);
      p4.x = radius*cos(ntheta2);  
      p4.y = southy;  
      p4.z = radius*sin(ntheta2);

      den = (p1.x*p1.x)+(p1.y*p1.y)+(p1.z*p1.z);  
      den = sqrt(den);

      if (den != 0.0) {

        t = radius / den; 
        p1.x *= t;  
        p1.y *= t;  
        p1.z *= t;

      }

      den = (p2.x*p2.x)+(p2.y*p2.y)+(p2.z*p2.z);  
      den = sqrt(den);

      if (den != 0.0) {

        t = radius / den; 
        p2.x *= t;  
        p2.y *= t;  
        p2.z *= t;

      }
      den = (p3.x*p3.x)+(p3.y*p3.y)+(p3.z*p3.z);  
      den = sqrt(den);
      if (den != 0.0) {

        t = radius / den; 
        p3.x *= t;  
        p3.y *= t;  
        p3.z *= t;

      }
      den = (p4.x*p4.x)+(p4.y*p4.y)+(p4.z*p4.z);  
      den = sqrt(den);
      if (den != 0.0) {

        t = radius / den; 
        p4.x *= t;  
        p4.y *= t;  
        p4.z *= t;

      }

      subdivide_tri(&p1,&p2,&p3,freq);
      subdivide_tri(&p3,&p2,&p4,freq);

    }

    return;

  }

#define norm_pt(v) { register double r = sqrt( ( ( v )->x * ( v )->x ) + \
                                               ( ( v )->y * ( v )->y ) + \
                                               ( ( v )->z * ( v )->z ) ); \
                     ( v )->x /= r; \
                     ( v )->y /= r; \
                     ( v )->z /= r; \
                    }

subdivide_tri(p1,p2,p3,a)

  struct Point_struct *p1, *p2, *p3;
  int a;

  {

    struct Point_struct n1, n2, n3;
    struct Point_struct p12, p13, p23;
    double den, t;

    if (a>0) {

      p12.x = (p1->x+p2->x)/2.0;
      p12.y = (p1->y+p2->y)/2.0;
      p12.z = (p1->z+p2->z)/2.0;
      den = (p12.x*p12.x)+(p12.y*p12.y)+(p12.z*p12.z);  
      den = sqrt(den);
      if (den != 0.0) {

        t = radius / den;
        p12.x *= t;  
        p12.y *= t;  
        p12.z *= t;

      }
      p13.x = (p1->x+p3->x)/2.0;
      p13.y = (p1->y+p3->y)/2.0;
      p13.z = (p1->z+p3->z)/2.0;
      den = (p13.x*p13.x)+(p13.y*p13.y)+(p13.z*p13.z); 
      den = sqrt(den);
      if (den != 0.0) {

        t = radius / den;
        p13.x *= t;  
        p13.y *= t;  
        p13.z *= t;

      }
      p23.x = (p2->x+p3->x)/2.0;
      p23.y = (p2->y+p3->y)/2.0;
      p23.z = (p2->z+p3->z)/2.0;
      den = (p23.x*p23.x)+(p23.y*p23.y)+(p23.z*p23.z);  
      den = sqrt(den);
      if (den != 0.0) {

        t = radius / den;
        p23.x *= t;  
        p23.y *= t;  
        p23.z *= t;

      }
      subdivide_tri(p1,  &p12,&p13,a-1);
      subdivide_tri(&p12, p2, &p23,a-1);
      subdivide_tri(&p13,&p23, p3, a-1);
      subdivide_tri(&p12,&p23,&p13,a-1);

    } else {

      n1.x = p1->x;  
      n1.y = p1->y;  
      n1.z = p1->z;  
      norm_pt(&n1);
      n2.x = p2->x;  
      n2.y = p2->y;  
      n2.z = p2->z;  
      norm_pt(&n2);
      n3.x = p3->x;  
      n3.y = p3->y;  
      n3.z = p3->z;  
      norm_pt(&n3);

/* nothing special about this poly */

      printf( "%f %f %f  %f %f %f\n", p1->x + xorg,
                                      p1->y + yorg,
                                      p1->z + zorg,
                                      n1.x, n1.y, n1.z );
      printf( "%f %f %f  %f %f %f\n", p2->x + xorg,
                                      p2->y + yorg,
                                      p2->z + zorg,
                                      n2.x, n2.y, n2.z );
      printf( "%f %f %f  %f %f %f\n", p3->x + xorg,
                                      p3->y + yorg,
                                      p3->z + zorg,
                                      n3.x, n3.y, n3.z );

    }

    return;

  }

Classify the following data: IK> Nntp-Posting-Host: elm

IK> I know it sounds stupid, but....

IK> Does anyone know how to control individual mouse buttons.
IK> I am writing an application, which currently makes use of
IK> ButtonPressMask, however this is invoked by the pressing
IK> of any mouse button. I want to be able to restrict this
IK> to one of the 3 buttons. I tried Button1PressMask, but
IK> just get undefined errors. I know it can be done, and I
IK> am pretty sure theres an easy answer to it, but I just don't know
IK> what it is.
IK> "time is of the essence ..."

There is no event mask for a particular mouse button press. However in your
event handler you can use the event structure passed in and query it to find
which button was pressed i.e.

void
SomeEventHandler(Widget w, XtPointer data, XEvent *event)
{
    /* Catch the button 1 (usually left button) */
    if ( event->xbutton.button == Button1 ) {
        /* do some action */
    } else {
        /* do nothing and exit from function */
    } 
}

Classify the following data: Contents:Problems with table widgets in R5

	The following part of a program (an user interface for a simulation
	system) did work in R4, but refused to in R5. Of cause, the R4-version
	did not know about the xpTableWidgetClass (we used tableWidgetClass 			instead - caught from the net in times of R3) and XpTableChildPosition 			(formerly XtTblPosition).
	Since compiling with R5, the program causes a zero width or height error
	(on sparc-stations). The trouble-shooter is the (re)computation of the
	model_init_table - table widget: though its childs (label and asciiText 
	widgets) exist, XtQueryGeometry returns a prefered width and height of 			zero. 
	Thus the following asignment cannot perform anything else but set the 			width and height of the newly created widget to zero. No wonder XtPopup 		or XtManage- Child create zero width or height errors (dependent on 			whether width and height of the shell widget are set in the resource 			file or not).

	Question: Does anyone know, why XtQueryGeometry returns so low prefered
			values when working on table widgets or perhaps what
			to do about?

	I'll be happy, if someone is able to help me. 
	Germans are requested to answer in german.

	Siegfried Kaiser
	email: kaiser@uniko.uni-koblenz.de


	The part of interest:


	/* Graphischer Neuaufbau des Model-Init-Formulars */
		/* Storing the old width and height of the viewport-	*/
		/* widget, which is the parent of the troubling table	*/
		/* widget, before the viewport widget is destroyed	*/
		/* The destroying of widgets before resizing them is a	*/
		/* relict from R3-age					*/

	if (model_init_popped_up) 
		XtUnmapWidget(model_init_form_view);
	XtDestroyWidget(model_init_form_view);

		/* Creating the subtree within the shell, of which the	*/
		/* root is the viewport widget				*/

	n = 0;
	XtSetArg(args[n],XtNfromVert,model_init_title); n++;
	XtSetArg(args[n],XtNfromHoriz,model_init_button_view); n++;
	XtSetArg(args[n],XtNallowVert,True); n++;
	XtSetArg(args[n],XtNforceBars,True); n++;
	model_init_form_view = XtCreateWidget("form_view",viewportWidgetClass,
					      model_init_form,args,n);

	n = 0;
	model_init_table = XtCreateWidget("table",xpTableWidgetClass,
					  model_init_form_view,args,n);

		/* create_form_widget does create and position table	*/
		/* widget's childs. To position them it uses XpTable-	*/
		/* ChildPosition in R5 and XtTblPosition in R4		*/

	create_form_widget(ptr_model_init_obj,model_init_table);

		/* 							*/
		/* The crucial function call:				*/
		/* intended to return the maximum height possible: if	*/
		/* there isn't sufficient space to show the whole table	*/
		/* widget, then the viewport shall grow as large as 	*/
		/* possible, but not beyond the border of screen.	*/
		/* If there is enough space, the window is intended to	*/
		/* shrienk to the smallest possible height.		*/
		/*							*/

	XtQueryGeometry(model_init_table,NULL,&pref);

